mapper.select:
select
  id, name, code, purchase_price, sale_price, unit
from products


        and id = #{id}


        and name = #{name}


        and code = #{code}


        and purchase_price = #{purchasePrice}


        and sale_price = #{salePrice}


        and unit = #{unit}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from products


        and id = #{id}


        and name = #{name}


        and code = #{code}


        and purchase_price = #{purchasePrice}


        and sale_price = #{salePrice}


        and unit = #{unit}
