mapper.select:
select
  id, product_id, sale_time, quantity, unit_price, total_price, salesman_name
from sales_records


        and id = #{id}


        and product_id = #{productId}


        and sale_time = #{saleTime}


        and quantity = #{quantity}


        and unit_price = #{unitPrice}


        and total_price = #{totalPrice}


        and salesman_name = #{salesmanName}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sales_records


        and id = #{id}


        and product_id = #{productId}


        and sale_time = #{saleTime}


        and quantity = #{quantity}


        and unit_price = #{unitPrice}


        and total_price = #{totalPrice}


        and salesman_name = #{salesmanName}
